const typeDefinitions = `
  directive @auth on QUERY | FIELD_DEFINITION | FIELD

  type Post {
    id: Int
    text: String
    user: User
  }

  type User {
    id: Int
    avatar: String
    username: String
  }

  type Message {
    id: Int
    text: String
    chat: Chat
    user: User
  }

  type Chat {
    id: Int
    messages: [Message]
    lastMessage: Message
    users: [User]
  }

  type PostFeed {
    posts: [Post]
  }

  type RootQuery {
    currentUser: User @auth
    posts: [Post]
    chats: [Chat] @auth
